import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  isAuth = false;
  lastUpdate = new Promise(
    (resolve, reject) => {
      const date = new Date();
      setTimeout(
          () => {
            resolve(date);
          }, 2000
        )
    }
  );
  appareils = [
    {id: 1, name:'Machine à laver', status:'éteint'},
    {id: 2, name:'TV', status:'allumé'},
    {id: 3, name:'Ordinateur', status:'éteint'}
  ];
  constructor() {
    setTimeout(
        () => {
          this.isAuth = true;
        }, 4000
      );
  }
  onAllumer() {
    alert('onAlluler method from app.component');
  }
}
